import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import Post from './post.js';
import Login from './login.js';
import CreatePost from './createPost.js';
import { BaseUrl } from './constants.js';


function getPosts(setPosts)
{
    fetch(BaseUrl + '/posts')
        .then(response => response.json())
        .then(result => {
            setPosts(result.data.posts);
        })
        .catch(console.error);

}

const AlreadyIn = ({setToken}) =>{
    return<div>
        <p>You are logged in, would you like to log out?</p>
        <button type = "button" onClick = {() => {
            setToken("");
            localStorage.setItem("token", "");}} >
                Log out</button>
    </div>
}

function createHeader(token, setHeader)
{
    console.log("lll")
    if(token)
    {
        console.log("dsfasdf")
        setHeader({
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + token
          })
    }
    else{
        setHeader(
            {
                'Content-Type': 'application/json',
            }
        )
    }
}


const App = () => {
    const [posts, setPosts] = useState([]);
    const [token, setToken] = useState("");
    const [header, setHeader] = useState({
        'Content-Type': 'application/json',
      })
    useEffect(() => {
        const t = localStorage.getItem("token");
        if(t) setToken(t);
        getPosts(setPosts);
        console.log(posts);
    }, []);
  return <Router >
      {!!token ? <div>Welcome User</div> : null}
      <Route path = "/posts">
        {posts.map((element, index) => {
            return<Post key = {index} element = {element} />})}
      </Route>
      <Route path = "/login">
         { !!token ? <AlreadyIn setToken = {setToken}/> : <Login mode = "login" setToken = {setToken} />}
        </Route>
      <Route path = "/register">
          {!!token ? <AlreadyIn setToken = {setToken}/> : <Login mode = "register" setToken = {setToken}/>}
        </Route>
      <Route path = "/newpost">
            <CreatePost token = {token} header = {header} setHeader = {setHeader} createHeader = {createHeader}/>
      </Route>
  </Router>
}
ReactDOM.render(
  <App />,
  document.getElementById('app'),
);
