import React, {useState} from 'react';
import { BaseUrl } from './constants';


function postMatches(post, text) {
    console.log(post.title);
    if(post.title.includes(text))
        {   console.log("match")
            return true;}
    if(post.description.includes(text)) return true;
    return false;
}



const Search = ({setSearchTerm,searchTerm, setPostsToDisplay, posts}) => {
    let st = "";
    return(
        <form onSubmit = {(e) => {
            e.preventDefault();
        }}>
            <label>Search:</label>
            <input type = "text" onChange = {(e) => {
                st = e.target.value;
                e.target.value = st;
                setSearchTerm(st);
                const filteredPosts = posts.filter(post => postMatches(post, st));
                setPostsToDisplay(searchTerm.length ? filteredPosts : posts)

            }}/>
            <input type = "submit" />
        </form>
    )
}

export default Search;
