import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import Post from './post.js';
import Login from './login.js';
import { BaseUrl } from './constants.js';


function getPosts(setPosts)
{
    fetch(BaseUrl + '/posts')
        .then(response => response.json())
        .then(result => {
            setPosts(result.data.posts);
        })
        .catch(console.error);

}

const AlreadyIn = () =>{
    return<div>
        <p>You are logged in, would you like to log out?</p>
    </div>
}


const App = () => {
    const [posts, setPosts] = useState([]);
    const [token, setToken] = useState("");
    useEffect(() => {
        const t = localStorage.getItem("token");
        if(t) setToken(t);
        getPosts(setPosts);
        console.log(posts);
    }, []);
  return <Router >
      {!!token ? <div>Welcome User</div> : null}
      <Route path = "/posts">
        {posts.map((element, index) => {
            return<Post key = {index} element = {element} />})}
      </Route>
      <Route path = "/login">
         { !!token ? null : <Login mode = "login" setToken = {setToken} />}
        </Route>
      <Route path = "/register"><Login mode = "register"/></Route>
  </Router>
}
ReactDOM.render(
  <App />,
  document.getElementById('app'),
);
